def get_opt_val(sop):
    sp_title=""
    sp_name=""
    sp_price=""
    for sp in sop:
        tmp=sp.select("option")
        tmp_name=""
        tmp_price=""
        for it in tmp:
            if it['value']=="":
                tmp_title=it.contents[0].replace("-","").strip()
            else:
                tmp_aaa=it.contents[0].split("(")
                if len(tmp_aaa)==1:
                    tmp_p=str(0)
                else:
                    tmp_p=str(tmp_aaa[1]).replace("추가","").replace(",","").replace("원","").replace(")","").strip()
                if tmp_name=="":
                    tmp_name=tmp_aaa[0].strip()
                    tmp_price=str(tmp_p)
                else:
                    tmp_name=tmp_name + "," + tmp_aaa[0].strip()
                    tmp_price=tmp_price + "," + str(tmp_p)
        sp_title=sp_title + "\n" + tmp_title
        sp_name=sp_name + "\n" + tmp_name
        sp_price=sp_price + "\n" + tmp_price
    sp_title=sp_title.strip()
    sp_name=sp_name.strip()
    sp_price=sp_price.strip()
    tmp_arr=[sp_title,sp_name,sp_price]
    return(tmp_arr)

